mapper.select:
SELECT COUNT(id) as cnt
FROM mkmember
WHERE id=#{id}

SELECT memberno, memcateno, id, passwd, mname, tel, zipcode, address1, address2, mdate
FROM mkmember
WHERE memberno = #{memberno}

SELECT memberno, memcateno, id, passwd, mname, tel, zipcode, address1, address2, mdate
FROM mkmember
WHERE id = #{id}

mapper.update:
UPDATE mkmember 
SET mname=#{mname}, tel=#{tel}, zipcode=#{zipcode},
      address1=#{address1}, address2=#{address2}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM mkmember
WHERE memberno = #{memberno}  AND passwd=#{passwd}

mapper.update:
UPDATE mkmember
SET passwd= #{passwd}
WHERE memberno= #{memberno}

mapper.select:

   SELECT count(*) AS cnt
   FROM mkmember
   WHERE memcateno=#{memcateno}

SELECT COUNT(memberno) as cnt
FROM mkmember
WHERE id=#{id} AND passwd=#{passwd}

mapper.update:
UPDATE mkmember 
SET memcateno=#{memcateno}
WHERE memberno=#{memberno}
